import React, { useEffect, useState } from 'react'
import { Form, Input, Button } from 'antd';
import { UserOutlined } from '@ant-design/icons';
import './scss/mainRoomChat.css';
import { useHistory } from 'react-router';
import io from 'socket.io-client';
import { Select } from 'antd';


const { Option } = Select;
export const MainRomChat = () => {
    const PORRT = 'localhost:3333';

    const history = useHistory()
    const [form] = Form.useForm();
    const [, forceUpdate] = useState({});
    const [roomChat, setRoomChat] = useState({
        room: '',
        userRoom: ''
    })

    useEffect(() => {
        let socket = io(PORRT);
        socket.emit('join room', { room: roomChat.room, userRoom: roomChat.userRoom })
        socket.on("disconnect", (reason) => {
            console.log(reason);
           });
    }, [roomChat.room, roomChat.userRoom]);
    // To disable submit button at the beginning.

    useEffect(() => {
        forceUpdate({});
    }, []);

    const onFinish = (values) => {
        setRoomChat({
            room: values.roomChat,
            userRoom: values.userName,
        })
        history.push('/chatapp')
    };

    return (
        <div className="mainRoom">
            <div>
                <h1>Room Chat Lẩu Xanh</h1>
                <div className="roomChat">
                    <Form form={form} name="horizontal_login" onFinish={onFinish}>

                        <Form.Item
                            name="roomChat"
                            rules={[{ required: true, message: 'Please input your username!' }]}
                        >
                            <Select placeholder="Select Room" style={{ width: '100%' }} >
                                <Option value="fe01">FE01</Option>
                                <Option value="fe02">FE02</Option>
                                <Option value="fe03">FE03</Option>
                            </Select>
                        </Form.Item>
                        <Form.Item
                            name="userName"
                            rules={[{ required: true, message: 'Please input your username!' }]}
                        >
                            <Input prefix={<UserOutlined className="site-form-item-icon" />} placeholder="Username" />
                        </Form.Item>

                        <Form.Item shouldUpdate>
                            {() => (
                                <Button
                                    type="danger"
                                    htmlType="submit"
                                    align="center"
                                    disabled={
                                        !form.isFieldsTouched(true) ||
                                        !!form.getFieldsError().filter(({ errors }) => errors.length).length
                                    }
                                >
                                    Join Room
                                </Button>
                            )}
                        </Form.Item>
                    </Form>
                </div>
            </div>
        </div>
    )
}
