import React, { useEffect, useState } from 'react'
import { Form, Input, Button } from 'antd';
import { UserOutlined, HomeTwoTone } from '@ant-design/icons';
import './scss/mainRoomChat.css';
import { useHistory } from 'react-router';
import io from 'socket.io-client';
import queryString from 'querystring';

export const MainRomChat = ({ location }) => {
    const PORRT = 'localhost:3333'
    const history = useHistory()
    const [form] = Form.useForm();
    const [, forceUpdate] = useState({});
    const [roomChat, setRoomChat] = useState({
        room: '',
        userRoom: ''
    })

    useEffect(() => {
        let socket = io(PORRT);
        socket.emit('join room', { room: roomChat.room, userRoom: roomChat.userRoom })
    }, [location.search, roomChat.room, roomChat.userRoom]);
    // To disable submit button at the beginning.
    useEffect(() => {
        forceUpdate({});
    }, []);

    const onFinish = (values) => {
        setRoomChat({
            room: values.roomChat,
            userRoom: values.userName,
        })
        // history.push('/chatapp')
    };

    return (
        <div className="mainRoom">
            <div className="roomChat">
                <h1>Room Chat Láº©u Xanh</h1>
                <Form form={form} name="horizontal_login" layout="inline" onFinish={onFinish}>
                    <Form.Item
                        name="roomChat"
                        rules={[{ required: true, message: 'Please input your username!' }]}
                    >
                        <Input prefix={<HomeTwoTone className="site-form-item-icon" />} placeholder="Room Chat" />
                    </Form.Item>
                    <Form.Item
                        name="userName"
                        rules={[{ required: true, message: 'Please input your username!' }]}
                    >
                        <Input prefix={<UserOutlined className="site-form-item-icon" />} placeholder="Username" />
                    </Form.Item>

                    <Form.Item shouldUpdate>
                        {() => (
                            <Button
                                type="primary"
                                htmlType="submit"
                                disabled={
                                    !form.isFieldsTouched(true) ||
                                    !!form.getFieldsError().filter(({ errors }) => errors.length).length
                                }
                            >
                                Log in
                            </Button>
                        )}
                    </Form.Item>
                </Form>
            </div>
        </div>
    )
}
