import { DISCONECT } from "./chatApp.const"

const initialState = {
    disconect: 0
}

export const ChatAppReducer = (state = initialState, { type, payload }) => {
    switch (type) {
        case DISCONECT: {
            if (payload.type) {
                state.disconect = 500
            }
            return { ...state, ...payload }
        }
        default:
            return state
    }
}
