import React, { useEffect, useState } from 'react'
import { Form, Input, Button } from 'antd';
import { UserOutlined, HomeTwoTone } from '@ant-design/icons';
import './scss/mainRoomChat.css';
import { useHistory } from 'react-router';
import io from 'socket.io-client';

export const MainRomChat = () => {
    const PORRT = 'localhost:3333'
    const history = useHistory()
    const [form] = Form.useForm();
    const [, forceUpdate] = useState({});

    useEffect(() => {
        const socket = io(PORRT);
        socket.on('join room', (userList) => {
            console.log(userList)
        })
    }, []);
    // To disable submit button at the beginning.
    useEffect(() => {
        forceUpdate({});
    }, []);

    const onFinish = (values) => {
        console.log('Finish:', values);
        history.push('/chatapp')
    };

    return (
        <div className="mainRoom">
            <div className="roomChat">
                <h1>Room Chat Láº©u Xanh</h1>
                <Form form={form} name="horizontal_login" layout="inline" onFinish={onFinish}>
                    <Form.Item
                        name="roomChat"
                        rules={[{ required: true, message: 'Please input your username!' }]}
                    >
                        <Input prefix={<HomeTwoTone className="site-form-item-icon" />} placeholder="Room Chat" />
                    </Form.Item>
                    <Form.Item
                        name="userName"
                        rules={[{ required: true, message: 'Please input your username!' }]}
                    >
                        <Input prefix={<UserOutlined className="site-form-item-icon" />} placeholder="Username" />
                    </Form.Item>

                    <Form.Item shouldUpdate>
                        {() => (
                            <Button
                                type="primary"
                                htmlType="submit"
                                disabled={
                                    !form.isFieldsTouched(true) ||
                                    !!form.getFieldsError().filter(({ errors }) => errors.length).length
                                }
                            >
                                Log in
                            </Button>
                        )}
                    </Form.Item>
                </Form>
            </div>
        </div>
    )
}
