/* eslint-disable react-hooks/exhaustive-deps */
import React, { useEffect, useState } from 'react'
import './scss/chatapp.css'
import { Layout, Menu, Breadcrumb, notification, Input, Tooltip, Form } from 'antd';
import { RollbackOutlined, UserOutlined, SmileOutlined, SendOutlined } from '@ant-design/icons';
import { Link, useLocation } from 'react-router-dom';
import io from 'socket.io-client';
// import { useSelector } from 'react-redux';
import Qs from 'qs'
let socket;
const { Header, Content, Footer, Sider } = Layout;
const { SubMenu } = Menu;

export const Chatapp = () => {
  const PORRT = 'localhost:3333';
  const [form] = Form.useForm();
  const [collapsed, setCollapsed] = useState(false)
  const [userList, setUserList] = useState([])
  const [notify, setNotify] = useState(null)
  const [errorAcknow, setErrorAcknow] = useState(null)
  const [rooms, setRoom] = useState(null)
  const [sendMessage, setSendMessage] = useState(null)
  const [receiverMessage, setReceiverMessage] = useState(null)


  const queryString = useLocation()

  const onCollapse = (collapsed) => {
    setCollapsed(collapsed)
  };

  useEffect(() => {
    socket = io(PORRT);
    const params = Qs.parse(queryString.search, { ignoreQueryPrefix: true })
    const { room, userRoom } = params;
    setRoom(room)
    // join room
    socket.emit('join room', { room, userRoom })
    // render list member
    socket.on('send list client inside room', (userList) => {
      setUserList(userList)
    })

    // semd message
    socket.emit("send message", sendMessage, acknowLedGements)
    // reciver message
    socket.on('send message', (receiverMessage) => {
      setReceiverMessage(receiverMessage)
    })
    // disconecet
    return () => {
      socket.disconnect()
    }
  }, [PORRT, queryString.search, sendMessage])

  useEffect(() => {
    // display notify
    socket.on('send message notify', (message) => {
      setNotify(message)
    })
  }, [])




  useEffect(() => {
    if (notify !== null) {
      openNotificationJoin()
    }
    setNotify(null)
  })

  useEffect(() => {
    if (errorAcknow !== null) {
      openNotificationAcknow()
    }
    setErrorAcknow(null)
  })

  const openNotificationJoin = () => {
    notification.open({
      message: `${notify}`,
      icon: <SmileOutlined style={{ color: '#108ee9' }} />,
    });
  };

  const openNotificationAcknow = () => {
    notification.open({
      message: `${errorAcknow}`,
      icon: <SmileOutlined style={{ color: '#e91010' }} />
    });
  };

  console.log("notify", notify);
  console.log("receiverMessage", receiverMessage);

  const onSendMessage = (e) => {
    if (e.message !== "") {
      setSendMessage(e.message)
    }
  }

  const acknowLedGements = (error) => {
    if (error) {
      setErrorAcknow(error)
      setSendMessage(null)
    }
  }

  return (
    <Layout style={{ minHeight: '100vh' }}>
      <Sider collapsible collapsed={collapsed} onCollapse={onCollapse}>
        <div className="logo" />
        <Menu theme="dark" defaultSelectedKeys={['1']} mode="inline">
          <Menu.Item key={userList.length + 1} icon={<RollbackOutlined />}>
            <Link to='/'>Come Back</Link>
          </Menu.Item>
          <SubMenu key="sub1" icon={<UserOutlined />} title="Member">
            {userList.map((item, index) => {
              return (
                <Menu.Item key={index}>{item.userName}</Menu.Item>
              )
            })}
          </SubMenu>
        </Menu>
      </Sider>
      <Layout className="site-layout">
        <Header className="site-layout-background" style={{ padding: 0 }}>Room Chat : {rooms?.toUpperCase()}</Header>
        <Content style={{ margin: '0 16px' }}>
          <Breadcrumb style={{ margin: '16px 0' }}>
            <Breadcrumb.Item>Message</Breadcrumb.Item>
          </Breadcrumb>
          <div className="site-layout-background" style={{ padding: 24, minHeight: 360 }}>
            Bill is a cat.
          </div>
          <Form form={form} name="horizontal_login" onFinish={onSendMessage}>
            <Form.Item name="message">
              <Input
                placeholder="Enter Message"
                prefix={<SendOutlined type="user" style={{ color: 'rgba(0,0,0,.25)' }} />}
                suffix={
                  <Tooltip title="Extra information" htmlType="submit">
                    <SendOutlined type="info-circle" style={{ color: 'rgba(0,0,0,.45)' }} />
                  </Tooltip>
                }
              />
            </Form.Item>
          </Form>
        </Content>
        <Footer style={{ textAlign: 'center' }}>Vũ Duy Anh Design ©2021 Created Chat_App</Footer>
      </Layout>
    </Layout>
  )
}
