import React, { useEffect, useState } from 'react'
import './scss/chatapp.css'
import { Layout, Menu, Breadcrumb } from 'antd';
import { RollbackOutlined, UserOutlined } from '@ant-design/icons';
import { Link } from 'react-router-dom';
import io from 'socket.io-client';

const { Header, Content, Footer, Sider } = Layout;
const { SubMenu } = Menu;

export const Chatapp = () => {
  const PORRT = 'localhost:3333'
  const [collapsed, setCollapsed] = useState(false)
  const [userList, setUserList] = useState([])

  const onCollapse = (collapsed) => {
    setCollapsed(collapsed)
  };

  useEffect(() => {
    let socket = io(PORRT);
    socket.on('send list client inside room', (userList) => {
      console.log(userList);
    })
  })

  return (
    <Layout style={{ minHeight: '100vh' }}>
      <Sider collapsible collapsed={collapsed} onCollapse={onCollapse}>
        <div className="logo" />
        <Menu theme="dark" defaultSelectedKeys={['1']} mode="inline">
          <Menu.Item key="1" icon={<RollbackOutlined />} >
            <Link to='/'>Come Back Room</Link>
          </Menu.Item>
          <SubMenu key="sub1" icon={<UserOutlined />} title="Member">
            <Menu.Item key="3">Tom</Menu.Item>
            <Menu.Item key="4">Bill</Menu.Item>
            <Menu.Item key="5">Alex</Menu.Item>
          </SubMenu>
        </Menu>
      </Sider>
      <Layout className="site-layout">
        <Header className="site-layout-background" style={{ padding: 0 }}>Room Chat</Header>
        <Content style={{ margin: '0 16px' }}>
          <Breadcrumb style={{ margin: '16px 0' }}>
            <Breadcrumb.Item>Message</Breadcrumb.Item>
          </Breadcrumb>
          <div className="site-layout-background" style={{ padding: 24, minHeight: 360 }}>
            Bill is a cat.
          </div>
        </Content>
        <Footer style={{ textAlign: 'center' }}>Vũ Duy Anh Design ©2021 Created Chat_App</Footer>
      </Layout>
    </Layout>
  )
}
